import React from 'react'
import Square from './square'
import './Board.css'
class Board extends React.Component{
    constructor(props)
    {
        super(props);
        this.state={
            sqaures: Array(9).fill(null),
        };
    }
    renderSquare(i)
    {
        return <Square value={this.state.sqaures[i]}
                       onClick={() => this.handleClick(i)}/>
    }
    render()
    {
    return (
        <div>
        <div className="rows">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
        </div>
        <div className="rows">
        {this.renderSquare(3)}
        {this.renderSquare(4)}
        {this.renderSquare(5)}
    </div>
    <div className="rows">
        {this.renderSquare(6)}
        {this.renderSquare(7)}
        {this.renderSquare(8)}
    </div>
    </div>
    )
}
}

export default Board
